import React, { Component} from 'react'

export default class Stats extends Component {
    
    render() {
        const gamestate = this.props.gamestate;
        const isPlayer_one = this.props.isPlayer_one;

        return (
            <div className="stats">
                {gamestate.p1_turn === isPlayer_one &&
                    
                    <h3>Your Turn</h3>}
            </div>
        )
    }
}
