import React, { Component } from 'react'
import {motion,AnimatePresence} from 'framer-motion';

export default class Lobby extends Component {
    static loadingContainerVariants = {
        start: {
          transition: {
            staggerChildren: 0.2,
          },
        },
        end: {
          transition: {
            staggerChildren: 0.2,
          },
        },
      }

      sloadingCircleVariants = {
        start: {
          y: "0%",
        },
        end: {
          y: "100%",
        },
      }
    loadingCircleTransition = {
        duration: 0.5,
        yoyo: Infinity,
        ease: "easeInOut",
      }
      
      
    
    render() {
        return (
            <div className="waiting-lobby" >
                <h5>Waiting for someone to join</h5>
                <motion.div
                    style={loadingContainer}
                    variants={loadingContainerVariants}
                    initial="start"
                    animate="end"
                    >
                    <motion.span
                        style={loadingCircle}
                        variants={loadingCircleVariants}
                        transition={loadingCircleTransition}
                    />
                    <motion.span
                        style={loadingCircle}
                        variants={loadingCircleVariants}
                        transition={loadingCircleTransition}
                    />
                    <motion.span
                        style={loadingCircle}
                        variants={loadingCircleVariants}
                        transition={loadingCircleTransition}
                    />
                    </motion.div>
                
            </div>
        )
    }
}
