import React, {Component} from 'react';
import {Button} from 'reactstrap';
import { connect } from 'react-redux';
import {player_input,cpu_input} from './../actions/actions';
import BestMove from './../AI';

class Square extends Component{


    renderValue = (val) => { //converts a value index to a string
        if (val === "1"){
            //return "✖"
            return "✕"
            
        }
        else if (val === "-1"){
            return "◯"
            //return "○"
        }
        else{
            return ""
        }
    }
    

    render(){
        return(
        
            <div className="square-inner" onClick = {this.props.player_input.bind(this,this.props.index)}>
                {this.renderValue(this.props.val)}
            </div>
        
        )
    }
}

const mapStateToProps = (state) => {
    return {
        grid: state.grid
    }
}

export default connect(mapStateToProps,{player_input,cpu_input})(Square)
