import React, { Component } from 'react'
import {motion,AnimatePresence} from 'framer-motion';
import {Button} from 'reactstrap';

export default class Lobby extends Component {
    state = {
      loading:true,
      code: "F33T"
    }
    
    render() {
      return(
        <div>
          {this.state.loading && <Waiting code={this.state.code}/>}
        </div>
      )
    }
}

class Waiting extends Component{
  render(){
    return (
      <AnimatePresence>
        <motion.div className="waiting-lobby" initial={{opacity:0}} animate={{opacity:1}} exit={{}} >
            <h5>Waiting for someone to join</h5>
            <h6>Session Code:</h6>
            <Button>{this.props.code}</Button>
            
        </motion.div>
      </AnimatePresence>
    )
  }
}
