import React, { Component } from 'react'
import Landing from "./landing";
import CreateSessionPage from './createSession';
import JoinSession from './joinSession';
import Lobby from './Lobby';
import socket from './../apis/port';
import io from 'socket.io-client';



export default class Container extends Component {
    constructor(props) {
        super(props)

        this.state = {
            landing:true,
            create:false,
            join:false,
            lobby:false,

            pl_one_name:"",
            lobby_waiting:true,
            code:"",
            isPlayer_one:false,

            gameState:undefined

        }
    }

    componentDidMount=()=>{
        socket.on("session-created",(name,code)=>{this.setState({
            landing:false,
            lobby:true,
            create:false,

            pl_one_name: name,
            code:code,
            isPlayer_one:true //if you created the session, you are player one. 

        })})

        socket.on("valid-code",(gameState)=>{
            
            console.log(gameState);
            console.log(this.state.isPlayer_one);
            this.setState({
                lobby_waiting: false,
                landing:false,
                create:false,
                join:false,
                lobby:true,
                gameState: gameState

           })
           
            
           
        })
    }
    
    RouteToCreatePage =()=>{
        this.setState({
            landing:false,
            create:true
        })
    }

    RouteToLobby = () => {
        this.setState({
            create:false,
            lobby:true
        })
    }

    RouteToLanding = () => {
        this.setState({
            landing:true,
            create:false,
            join:false,
            lobby:false

        })
    }


    RouteToJoinPage=()=>{
        this.setState({
            landing:false,
            create:false,
            join:true,
            lobby:false

        })
    }
    render() {
        
        const gamestate = this.state.gameState;

        return (
            <div>
                {this.state.landing && <Landing create={this.RouteToCreatePage} join={this.RouteToJoinPage}/>}
                {this.state.create && <CreateSessionPage to={this.RouteToLobby} back={this.RouteToLanding}/>}
                {this.state.join && <JoinSession back={this.RouteToLanding}/>}
                {this.state.lobby && <Lobby gamestate={gamestate} waiting={this.state.lobby_waiting} code={this.state.code} isPlayer_one={this.state.isPlayer_one} /> }
                
            </div>
        )
    }
}


//note to self: constructor
