import React, { Component } from 'react';
import {InputGroup,InputGroupAddon,Input,InputGroupText, Button} from 'reactstrap';
import {motion,AnimatePresence} from 'framer-motion';

export default class joinSession extends Component {
    state = {
        "code": "",
        "name": ""
    }
    updateForm = (event) => {
        
        this.setState({
            [event.target.name]: event.target.value
        })
        
    }

    submitForm = () => {
        if(this.state["code"] !== "" && this.state["name"] !== "" ){
            console.log(this.state);
            //TODO connect to backend
        }
    }

    render() {
        return (
            <AnimatePresence>
            <motion.div className="session-page" initial={{opacity:0}} animate={{opacity:1}} exit={{opacity:0}}>
                <InputGroup style={{width:"90%", margin:"0 auto"}}>
                    <Input onChange={this.updateForm} name="code" placeholder="session code"/>
                </InputGroup>
                <InputGroup style={{width:"90%", margin:"0 auto"}}>
                    <Input onChange={this.updateForm} name="name" placeholder="username"/>
                </InputGroup>
                <Button onClick={this.submitForm} className="create-session-btn" color="primary">Join Session</Button>
                <Button className="session-btn" style={{width:"10%"}} color="primary" onClick={this.props.back}>&lt; </Button>
                
            </motion.div>
            </AnimatePresence>
        )
    }
}
