import React, { Component } from 'react';
import {InputGroup,InputGroupAddon,Input,InputGroupText, Button} from 'reactstrap';
import {motion,AnimatePresence} from 'framer-motion';
import socket from './../apis/port';
import io from 'socket.io-client';

export default class joinSession extends Component {
    state = {
        "code": "",
        "name": "",
        invalid: false
    }
    updateForm = (event) => {
        
        this.setState({
            [event.target.name]: event.target.value
        })
        
    }

    submitForm = () => {
        if(this.state["code"] !== "" && this.state["name"] !== "" ){
            socket.emit("join-session",this.state);
        }
        // TODO: else give a prompt
    }

    componentDidMount(){
        socket.on("invalid-code",()=>{
            
        })
    }

    render() {
        return (
            <AnimatePresence>
            <motion.div style={{top:"35%"}} className="session-page" initial={{opacity:0}} animate={{opacity:1}} exit={{opacity:0}}>
                <InputGroup style={{width:"90%", margin:"0 auto"}}>
                    <Input onChange={this.updateForm} name="code" placeholder="session code"/>
                </InputGroup>
                <InputGroup style={{width:"90%", margin:"0 auto"}}>
                    <Input onChange={this.updateForm} name="name" placeholder="username"/>
                </InputGroup>
                <Button onClick={this.submitForm} className="session-btn" color="primary">Join Session</Button>
                <Button className="session-btn" style={{width:"10%"}} color="primary" onClick={this.props.back}>&lt; </Button>
                <motion.div initial={{opacity:0}} animate={{opacity:1}} exit={{opacity:0}}>
                    {this.state.invalid && <p style={{color:"red"}}>Invalid Session Code</p>}
                </motion.div>
                
            </motion.div>
            </AnimatePresence>
        )
    }
}
