import React, { Component } from 'react'
import Landing from "./landing";
import CreateSessionPage from './createSession';
import socket from './../apis/port';
import io from 'socket.io-client';



export default class Container extends Component {
    constructor(props) {
        super(props)

        this.state = {
            landing:true,
            create:false,
            join:false,
            lobby:false,

            pl_one_name:""

        }
    }

    componentDidMount=()=>{
        socket.on("session-created",(name)=>{this.setState({
            landing:false,
            lobby:true,
            create:false
        })})
    }
    
    RouteToCreatePage =()=>{
        this.setState({
            landing:false,
            create:true
        })
    }

    RouteToLobby = () => {
        this.setState({
            create:false,
            lobby:true
        })
    }

    RouteToLanding = () => {
        this.setState({
            landing:true,
            create:false,
            join:false,
            lobby:false

        })
    }
    render() {
        return (
            <div>
                {this.state.landing && <Landing to={this.RouteToCreatePage}/>}
                {this.state.create && <CreateSessionPage to={this.RouteToLobby} back={this.RouteToLanding}/>}
                
            </div>
        )
    }
}
