import React, { Component } from 'react';
import {InputGroup,InputGroupAddon,Input,InputGroupText, Button} from 'reactstrap';
import {motion,AnimatePresence} from 'framer-motion';
import {Link} from 'react-router-dom';
import io from 'socket.io-client';
//import PORT from './../apis/port';
import socket from './../apis/port';

//const socket = io('http://localhost:8000');

//socket.on("hello",msg=>{console.log(msg)});

export default class createSession extends Component {
    constructor(props){
        super(props);
        this.state = {
            player1_name:""
        };
        socket.on("hello",msg=>{console.log(msg);});
        
        
        
    }
    componentDidMount=()=>{
        console.log("component mounted");
        socket.on("hello",msg=>{console.log(msg);});
    }
    
    

    updateForm = (e) => {
        this.setState({
            player1_name: e.target.value
        }
        );

    }
    
    createSession = (e) => {
        if(this.state.player1_name !== ""){
            socket.emit("create-session",this.state.player1_name);
        }

        else{
            e.preventDefault();
        }
    }

    

    
      

    render() {
        return (
            <AnimatePresence>
            <motion.div className="session-page"  initial={{opacity:0}} animate={{opacity:1}} exit={{opacity:0}}>
                <InputGroup style={{width:"90%", margin:"0 auto"}}>
                    <Input placeholder="username" onChange={this.updateForm}/>
                </InputGroup>
                
                    <Button className="create-session-btn" color="primary" onClick={this.createSession}>Create & Join</Button>
                    <Button className="create-session-btn" style={{width:"10%"}} color="primary" onClick={this.props.back}>&lt; </Button>
                
            </motion.div>
            </AnimatePresence>
            
            
        )
    }
}
