import React, { Component } from 'react'
import Landing from "./landing";
import CreateSessionPage from './createSession';
import JoinSession from './joinSession';
import Lobby from './Lobby';
import socket from './../apis/port';
import io from 'socket.io-client';



export default class Container extends Component {
    constructor(props) {
        super(props)

        this.state = {
            landing:true,
            create:false,
            join:false,
            lobby:false,

            pl_one_name:"",
            lobby_waiting:true,
            code:"",
            isPlayer_one:true

        }
    }

    componentDidMount=()=>{
        socket.on("session-created",(name,code)=>{this.setState({
            landing:false,
            lobby:true,
            create:false,

            pl_one_name: name,
            code:code,
            isPlayer_one:true

        })})

        socket.on("valid-code",(gameState)=>{
            this.setState({
            lobby_waiting: false,

            landing:false,
            create:false,
            join:false,
            lobby:true,
            

            })
        })
    }
    
    RouteToCreatePage =()=>{
        this.setState({
            landing:false,
            create:true
        })
    }

    RouteToLobby = () => {
        this.setState({
            create:false,
            lobby:true
        })
    }

    RouteToLanding = () => {
        this.setState({
            landing:true,
            create:false,
            join:false,
            lobby:false

        })
    }


    RouteToJoinPage=()=>{
        this.setState({
            landing:false,
            create:false,
            join:true,
            lobby:false

        })
    }
    render() {
        return (
            <div>
                {this.state.landing && <Landing create={this.RouteToCreatePage} join={this.RouteToJoinPage}/>}
                {this.state.create && <CreateSessionPage to={this.RouteToLobby} back={this.RouteToLanding}/>}
                {this.state.join && <JoinSession back={this.RouteToLanding}/>}
                {this.state.lobby && <Lobby pl_one_name={this.state.pl_one_name} waiting={this.state.lobby_waiting} code={this.state.code} isPlayer_one={this.state.isPlayer_one} /> }
                
            </div>
        )
    }
}
